  background-color: #fff;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.mobile__navigations__menu-btn, .mobile__navigations__search-btn, .mobile__navigations__cart-btn {
  display: flex;
  justify-content: center;
  align-items: center;
}
.mobile__navigations__menu-btn img, .mobile__navigations__search-btn img, .mobile__navigations__cart-btn img {
  cursor: pointer;
  border-radius: 3px;
  padding: 13px;
  background-color: #f3f4f5;
}
.mobile .nav {
