  .mobile,
.shipping-details-mobile {
    display: none;
  }

  .desktop {
    display: block;
    height: auto;
  }
  .desktop__top-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 37px;
    background-color: #f3f4f5;
    position: relative;
  }
  .desktop__top-content__left, .desktop__top-content__right {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }
  .desktop__top-content__left .link, .desktop__top-content__right .link {
    font-weight: 500;
    font-size: 13px;
    line-height: 18px;
    color: #1b1b1b;
  }
  .desktop__top-content__left .link img, .desktop__top-content__right .link img {
    width: 14px;
    height: auto;
  }
  .desktop__top-content__right {
    display: flex;
    justify-content: end;
    align-items: center;
    width: 100%;
  }
  .desktop__top-content__right .link {
    position: relative;
    padding: 0 15px;
    border-right: 0.5px solid rgba(157, 157, 157, 0.3);
  }
  .desktop__top-content__right .link:nth-child(3) {
    border-right: none;
  }
  .desktop__navigations {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 1.2rem;
    background-color: #fff;
    width: 100%;
    height: 100px;
    top: 0;
  }
  .desktop__navigations__link {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__navigations__link a {
    order: 2;
  }
  .desktop__navigations__link .sticky-hamburger {
    cursor: pointer;
    display: none;
    margin-right: 0.5rem;
  }
  .desktop__navigations__search {
    width: 45%;
    display: flex;
    justify-content: start;
    align-items: center;
  }
  .desktop__navigations__search .search-field {
    border: 1px solid #9d9d9d;
    padding: 10px;
    width: 80%;
  }
  .desktop__navigations__search .btn {
    border: 1px solid #0161a5;
    padding: 10px;
    background-color: #0161a5;
    color: #fff;
    width: 20%;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
  }
  .desktop__navigations__search ::placeholder {
    color: #9d9d9d;
  }
  .desktop__navigations .account-cart {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__navigations .account-cart .account,
.desktop__navigations .account-cart .cart {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__navigations .account-cart .account__image,
.desktop__navigations .account-cart .cart__image {
    background-color: #f3f4f5;
    padding: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .desktop__navigations .account-cart .account__content a,
.desktop__navigations .account-cart .cart__content a {
    display: block;
    font-weight: 600;
    font-size: 16px;
    line-height: 22px;
    color: #1b1b1b;
    padding: 0 15px;
  }
  .desktop__navigations .account-cart .account__content a span,
.desktop__navigations .account-cart .cart__content a span {
    display: block;
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    color: #1b1b1b;
  }
  .desktop .sticky-desktop {
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 5;
    border-bottom: 1px solid rgba(157, 157, 157, 0.3);
  }
  .desktop .hamburger-icon-show {
    display: block;
  }
  .desktop__menus {
    background-color: #0161a5;
    position: relative;
    height: 68px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 1.2rem;
  }
  .desktop__menus__categories .toggle-category {
    font-weight: 600;
    position: relative;
    font-size: 16px;
    line-height: 22px;
    color: #fff;
    cursor: pointer;
    padding-right: 40px;
    border-right: 0.5px solid #fff;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__menus__categories .toggle-category img {
    margin-right: 1rem;
  }
  .desktop__menus__categories .toggle-category.show-menu {
    position: fixed;
    top: 3%;
    left: 0;
    color: #1b1b1b;
  }
  .desktop__menus__categories .category-menu {
    position: absolute;
    top: 100%;
    z-index: 5;
    display: none;
    height: auto;
    width: 340px;
    background-color: #f3f4f5;
    border: 2px solid #0161a5;
    border-top: none;
  }
  .desktop__menus__categories .category-menu__list,
.desktop__menus__categories .category-menu label {
    padding: 15px 0px;
    margin: 0 15px;
    border-bottom: 1px solid rgba(157, 157, 157, 0.3);
  }
  .desktop__menus__categories .category-menu__list__link,
.desktop__menus__categories .category-menu label__link {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__menus__categories .category-menu__list__link span,
.desktop__menus__categories .category-menu label__link span {
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .desktop__menus__categories .category-menu__list__link span img,
.desktop__menus__categories .category-menu label__link span img {
    margin-right: 0.5rem;
  }
  .desktop__menus__categories .category-menu__list:hover,
.desktop__menus__categories .category-menu label:hover {
    background-color: rgba(157, 157, 157, 0.1);
  }
  .desktop__menus__categories .category-menu label {
    display: block;
    cursor: pointer;
  }
  .desktop__menus__categories .category-menu label span {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .desktop__menus__categories .category-menu label span span img {
    margin-right: 0.5rem;
  }
  .desktop__menus__categories .category-menu #toggle-inner {
    display: none;
  }
  .desktop__menus__categories .category-menu .inner-menu-d {
    position: absolute;
    left: 100%;
    top: 27%;
    background-color: #f3f4f5;
    width: 300px;
    border: 1px solid #0161a5;
    display: none;
  }
  .desktop__menus__categories .category-menu .inner-menu-d__list {
    padding: 15px 0px;
    border-bottom: 1px solid rgba(157, 157, 157, 0.3);
    margin: 0 15px;
  }
  .desktop__menus__categories .category-menu .inner-menu-d__list:hover {
    background-color: rgba(157, 157, 157, 0.1);
  }
  .desktop__menus__categories .category-menu #toggle-inner:checked ~ .inner-menu-d {
    display: block;
  }
  .desktop__menus__categories .category-menu.show-static-menu {
    display: block;
    border-top: none;
  }
  .desktop__menus__categories .category-menu.show-fixed-menu {
    position: fixed;
    top: 11.8%;
    left: 2%;
    z-index: 4;
    display: block;
  }
  .desktop__menus__categories #desktop-category-toggle {
    display: none;
  }
  .desktop__menus__categories #desktop-category-toggle:checked ~ .category-menu {
    display: block;
  }
  .desktop__menus__nav {
    width: 52%;
  }
  .desktop__menus__nav .menu__list {
    display: inline-block;
    padding: 0 10px;
  }
  .desktop__menus__nav .menu__list__link {
    color: #fff;
    font-weight: 400;
    font-size: 16px;
    line-height: 22px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .desktop__menus__nav .menu__list__link img {
    width: 10px;
    height: auto;
    transform: rotate(180deg);
    margin-left: 0.3rem;
  }
  .desktop__menus__free-shipping span {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 16px;
    line-height: 22px;
    color: #fff;
  }
  .desktop__menus__free-shipping span img {
    margin-right: 0.5rem;
  }

  .footer__container {
    display: grid;
    align-items: start;
    grid-template-columns: 1.5fr 1fr 1fr 1fr 1fr;
  }
  .footer__container__box {
    border-bottom: none;
    padding-bottom: 0;
    margin-top: 1.5rem;
  }
  .footer__container__box p {
    padding: 15px 0;
  }
  .footer__container__box-list .toggle-list {
    border-bottom: none;
    pointer-events: none;
    padding: 10px 0;
  }
  .footer__container__box-list .toggle-list img {
    display: none;
  }
  .footer__container__box-list .list {
    display: block;
    border-bottom: none;
  }
  .footer__container__box-list .list a {
    margin: 1.5rem 0;
  }
  .footer .scroll-to {
    padding: 30px 0;
  }
  .footer .copy-right {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
}
@media screen and (min-width: 1200px) {
  .desktop__top-content {
    padding: 0 5rem;
  }
  .desktop__navigations {
    padding: 0 5rem;
  }
  .desktop__menus {
    padding: 0 5rem;
  }
  .desktop__menus .category-menu.show-fixed-menu {
    position: fixed;
    top: 11%;
    left: 5%;
    z-index: 4;
    display: block;
  }
  .hero {
    margin: 0 5rem;
    margin-top: 1.5rem;
  }
  .hero__container {
    padding: 3rem 8rem;
  }
  .hero__container__left .headline {
    font-weight: 700;
    font-size: 60px;
    line-height: 68px;
  }
  .hero__container__right__box .details .headline {
    font-weight: 700;
    font-size: 28px;
    line-height: 38px;
  }
  .hero__container__right__box .details p {
    font-weight: 500;
    font-size: 15px;
    line-height: 20px;
  }

  .footer {
    margin-top: 7rem;
    padding: 0 5rem;
  }
  .footer .scroll-to {
    padding: 0;
  }

  .categories,
.shop-category,
.valves-online,
.products__list,
.references-guides,
.experts {
    padding: 0 5rem;
    margin: 7rem auto;
  }

  .valves-online .headline {
    font-weight: 600;
    font-size: 27px;
    line-height: 37px;
  }
  .valves-online .description {
    font-weight: 300;
    font-size: 17px;
    line-height: 23px;
  }

  .experts__content h2 {
    font-weight: 600;
    font-size: 27px;
    line-height: 37px;
  }
  .experts__content p {
    font-weight: 300;
    font-size: 17px;
    line-height: 23px;
  }

  .products__list__product__heading label {
    pointer-events: none;
    border-bottom: none;
    position: relative;
  }
  .products__list__product__heading label:before {
    content: "";
    position: absolute;
    bottom: 0;
    left: 14%;
    background-color: rgba(157, 157, 157, 0.3);
    height: 1px;
    width: 150px;
  }
  .products__list__product .content p {
    font-weight: 300;
    font-size: 17px;
    line-height: 23px;
  }

  .references-guides__content__box {
    padding: 60px 50px;
  }
  .references-guides__content__box__content .description {
    font-weight: 400;
    font-size: 17px;
    line-height: 23px;
  }

  .free-shipping__content {
    padding: 0 5rem;
  }
  .free-shipping__content h2 {
    font-weight: 700;
    font-size: 30px;
    line-height: 41px;
  }
  .free-shipping__content p {
    font-weight: 500;
    font-size: 22px;
    margin-top: 0.5rem;
    line-height: 30px;
  }

  .shop-category__container__top, .shop-category__container__bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    gap: 20px;
  }
  .shop-category__container__top .box, .shop-category__container__bottom .box {
    border-right: 1px solid rgba(157, 157, 157, 0.3);
  }
  .shop-category__container__top .box:nth-child(6),
.shop-category__container__top .box:nth-child(12), .shop-category__container__bottom .box:nth-child(6),
.shop-category__container__bottom .box:nth-child(12) {
    border-right: none;
  }
  .shop-category__container__bottom {
    margin-top: 2rem;
  }

  .products__list {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 40px;
  }

  .motto__content {
    padding: 0 5rem;
  }

  .free-shipping {
    margin: 0 5rem;
  }
}

