// Breakpoints

@media screen and (min-width: 680px) {
  .footer {
    padding: $tablet-spacing;
    &__container {
      .copy-right {
        @include flex(space-between, center);
      }
    }
  }
  .mobile {
    &__top-content {
      padding: $tablet-spacing;
    }

    &__navigations {
      padding: $tablet-spacing;
    }
  }

  .categories,
  .shop-category,
  .valves-online,
  .products__list,
  .references-guides,
  .experts {
    padding: $tablet-spacing;
    margin: 7rem auto;
  }

  .hero {
    margin: $tablet-spacing;
  }

  .free-shipping {
    margin: $tablet-spacing;
  }

  // Hero

  .hero {
    background-image: url('./resources/hero-desktop-lg.jpg');
    background-size: cover;
    background-position: center center;
    margin-top: 2rem;

    &__container {
      @include flex(space-between, center);
      padding: 3rem 2rem;
      height: auto;

      &__left {
      }

      &__right {
        margin-top: 0;

        &__box {
          margin: 1rem 0;
        }
      }
    }
  }

  // free shipping

  .free-shipping {
    background-image: url('./resources/free-shipping-desktop.jpg');
    background-size: cover;
    background-position: center center;

    &__content {
      @include flex(start, center);
      flex-direction: row;
      text-align: left;

      &__image {
        margin-right: 1rem;
      }

      h2 {
        margin: 0;
      }
    }
  }

  // Motto

  .motto {
    background-image: url('./resources/motto-bg-desktop.jpg');
    background-size: cover;
    background-position: center center;

    &__content {
      @include flex(space-between, center);
      flex-wrap: wrap;
    }
  }

  // Products List

  .products__list {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 40px;

    &__product {
      margin-top: 0;
      cursor: auto;
      padding-top: 0;
      pointer-events: none;

      &__heading {
        #prod-arrow {
          display: none;
        }
      }

      .content {
        display: block;
      }
    }

    &__product:nth-child(1) {
      margin-top: -2.22rem;
    }
  }
}

@media screen and (min-width: 980px) {
  // Remove Mobile Menu And Create Desktop

  .mobile,
  .shipping-details-mobile {
    display: none;
  }

  // Desktop Menu

  .desktop {
    display: block;
    height: auto;

    &__top-content {
      @include flex(space-between, center);
      height: 37px;
      background-color: $second-clr;

      position: relative;

      &__left,
      &__right {
        @include flex(space-between, center);
        width: 100%;

        .link {
          font-weight: 500;
          font-size: 13px;
          line-height: 18px;
          color: $txt-clr;

          img {
            width: 14px;
            height: auto;
          }
        }
      }

      &__right {
        @include flex(end, center);
        width: 100%;

        .link {
          position: relative;
          padding: 0 15px;
          border-right: 0.5px solid rgba(157, 157, 157, 0.3);
        }

        .link:nth-child(3) {
          border-right: none;
        }
      }
    }

    &__navigations {
      @include flex(space-between, center);
      padding: $mobile-spacing;
      background-color: #fff;
      width: 100%;

      height: 100px;
      top: 0;

      &__link {
        @include flex(space-between, center);

        a {
          order: 2;
        }

        .sticky-hamburger {
          cursor: pointer;
          display: none;
          margin-right: 0.5rem;
        }
      }

      &__search {
        width: 45%;
        @include flex(start, center);

        .search-field {
          border: 1px solid #9d9d9d;
          padding: 10px;
          width: 80%;
        }

        .btn {
          border: 1px solid $primary-clr;
          padding: 10px;
          background-color: $primary-clr;
          color: #fff;
          width: 20%;
          @include flex(center, center);
          cursor: pointer;
        }

        ::placeholder {
          color: #9d9d9d;
        }
      }

      .account-cart {
        @include flex(space-between, center);

        .account,
        .cart {
          @include flex(space-between, center);

          &__image {
            background-color: $second-clr;
            padding: 10px;
            @include flex(center, center);
          }

          &__content {
            a {
              display: block;
              font-weight: 600;
              font-size: 16px;
              line-height: 22px;
              color: $txt-clr;
              padding: 0 15px;

              span {
                display: block;
                font-weight: 400;
                font-size: 12px;
                line-height: 16px;
                color: $txt-clr;
              }
            }
          }
        }
      }
    }

    .sticky-desktop {
      position: fixed;
      top: 0;
      width: 100%;
      z-index: 5;
      border-bottom: 1px solid rgba(157, 157, 157, 0.3);
    }

    .hamburger-icon-show {
      display: block;
    }

    &__menus {
      background-color: $primary-clr;
      position: relative;
      height: 68px;
      @include flex(space-between, center);
      padding: $mobile-spacing;

      &__categories {
        .toggle-category {
          font-weight: 600;

          position: relative;
          font-size: 16px;
          line-height: 22px;
          color: #fff;
          cursor: pointer;
          padding-right: 40px;
          border-right: 0.5px solid #fff;
          @include flex(space-between, center);

          img {
            margin-right: 1rem;
          }
        }

        .toggle-category.show-menu {
          position: fixed;
          top: 3%;
          left: 0;
          color: $txt-clr;
        }

        .category-menu {
          position: absolute;
          top: 100%;
          z-index: 5;
          display: none;
          height: auto;
          width: 340px;
          background-color: $second-clr;
          border: 2px solid $primary-clr;
          border-top: none;

          &__list,
          label {
            padding: 15px 0px;
            margin: 0 15px;
            border-bottom: 1px solid rgba(157, 157, 157, 0.3);

            &__link {
              @include flex(space-between, center);

              span {
                @include flex(center, center);

                img {
                  margin-right: 0.5rem;
                }
              }
            }

            &:hover {
              background-color: rgba(157, 157, 157, 0.1);
            }
          }

          label {
            display: block;
            cursor: pointer;

            span {
              @include flex(space-between, center);

              span {
                img {
                  margin-right: 0.5rem;
                }
              }
            }
          }

          #toggle-inner {
            display: none;
          }

          .inner-menu-d {
            position: absolute;
            left: 100%;
            top: 27%;
            background-color: $second-clr;
            width: 300px;
            border: 1px solid $primary-clr;
            display: none;

            &__list {
              padding: 15px 0px;
              border-bottom: 1px solid rgba(157, 157, 157, 0.3);
              margin: 0 15px;

              &:hover {
                background-color: rgba(157, 157, 157, 0.1);
              }
            }
          }

          #toggle-inner:checked ~ .inner-menu-d {
            display: block;
          }
        }

        .category-menu.show-static-menu {
          display: block;
          border-top: none;
        }

        .category-menu.show-fixed-menu {
          position: fixed;
          top: 11.8%;
          left: 2%;
          z-index: 4;
          display: block;
        }

        #desktop-category-toggle {
          display: none;
        }

        #desktop-category-toggle:checked ~ .category-menu {
          display: block;
        }
      }

      &__nav {
        width: 52%;

        .menu {
          &__list {
            display: inline-block;
            padding: 0 10px;

            &__link {
              color: #fff;
              font-weight: 400;
              font-size: 16px;
              line-height: 22px;
              @include flex(center, center);

              img {
                width: 10px;
                height: auto;
                transform: rotate(180deg);
                margin-left: 0.3rem;
              }
            }
          }
        }
      }

      &__free-shipping {
        span {
          @include flex(center, center);
          font-size: 16px;
          line-height: 22px;
          color: #fff;

          img {
            margin-right: 0.5rem;
          }
        }
      }
    }
  }

  // footer

  .footer {
    &__container {
      display: grid;
      align-items: start;
      grid-template-columns: 1.5fr 1fr 1fr 1fr 1fr;

      &__box {
        border-bottom: none;
        padding-bottom: 0;
        margin-top: 1.5rem;

        p 
        {
          padding: 15px 0;
        }
      }
      &__box-list {
        .toggle-list {
          border-bottom: none;
          pointer-events: none;
          padding: 10px 0;
          img {
            display: none;
          }
        }
        .list {
          display: block;
          border-bottom: none;

          a {
            margin: 1.5rem 0;
          }
        }
      }
    }

    .scroll-to {
      padding: 30px 0;
    }

    .copy-right {
      @include flex(space-between, center);
    }
  }
}

